function uploadImage(e){const t=e.target.attributes.name.value;document.getElementsByClassName(t)[0].classList.add("hidden");const n=document.querySelector(`[wrapper-name="${t}"]`);n?.classList.add("block"),n?.classList.remove("hidden");const s=e.target.files[0],a=document.querySelector(`[img-name="${t}"]`);a.classList.add("block"),a.classList.remove("hidden"),a.src=URL.createObjectURL(s)}function goTo(e){window.open(e,"_self")}document.addEventListener("alpine:init",(()=>{Alpine.store("openedModal",null)}));